oat:left;position:relative;width:2.5em;height:2.5em;background:#fff;border-radius:50%;color:transparent;margin:0 .5em;border:4px solid #47a3da;}\n.nav-btn-square a{display:block;color: white;float:left;position:relative;width:150px;height:35px;background:#666666;margin:0 5px 0 0;font-size: 18px;font-weight: 500;text-align: center;padding-top:5px;}\n.nav-btn-round a:after{content:attr(data-info);color:#47a3da;position:absolute;width:600%;top:120%;text-align:right;right:0;opacity:0;pointer-events:none;}\n\n.nav-btn-round a:hover:after,.nav-btn-square a:hover:after{opacity:1;}\n.nav-btn-round a:hover,.nav-btn-square a:hover{background:#47a3da;}\n.nav-btn-round a:hover:before,.nav-btn-square a:hover:before{color:#fff;}\n.nav-btn-square h3{text-align:left;margin-left:8px;color:#47a3da;padding-bottom:5px;}\n.nav-btn-round a.nav-current, .nav-btn-square a.nav-current{background:#47a3da;}\n.nav-btn-round a.nav-current:before, .nav-btn-square a.nav-current:before{color:#fff;}\n.nav-btn-square-large a{ width: 7em;height: 7em;}\n.icon:before{font-family:Flaticon;position:absolute;top:0;width:100%;height:100%;speak:none;font-size:2em;font-style:normal;font-weight:normal;line-height:1.3;text-align:center;color:#47a3da;-webkit-font-smoothing:antialiased;}\n.icon2:before{font-family:icomoon;position:absolute;top:0;width:100%;height:100%;speak:none;font-size:2em;font-style:normal;font-weight:normal;line-height:1.3;text-align:center;color:#47a3da;-webkit-font-smoothing:antialiased;}\n.sub-nav-title{ color: #47a3da;font-weight: 500;font-size: 16px;margin-bottom: 10px;margin-left: 5px;text-decoration: underline;}\n\n/* Admin Chooser\n================================================== */\n.nav-btn-square-large a{ width: 4em;height: 4em;text-align: center;margin-top: 5px;margin-left: 20px;}\n.nav-btn-square-large{ padding-bottom: 40px;margin-left: 30px;margin-top: 5px;padding-right: 5px;}\n.chooser-text {padding-top:60px;display:inline-block;color: #444;font-size: 14px;}\n.margin-top-min20{ margin-top: -20px;}\n.no-margin{ margin: 0;}\n\n\n/* Confirm Slide In Top\n================================================== */\n.confirm-box-inner h4{color:#fff;text-align:center;margin-bottom: 30px;}\n.confirm-box-inner{margin:30px auto;width:600px;}\n.confirm-box-btns{width:350px;margin:0 auto;text-align:center;}\n.confirm-box-outer{background:#666666;position:fixed;color:#fff;top:-200px;width:100%;height:200px;left:0;z-index:1999;overflow:hidden;}\n.confirm-box-outer.confirm-box-outer-open{top:0;}\n.confirm-box-outer-push{overflow-x:hidden;position:relative;left:0;}\n.confirm-box-outer,.confirm-box-outer-push{-webkit-transition:all .3s ease;-moz-transition:all .3s ease;transition:all .3s ease;}\n\n/* Gridviews\n================================================== */\ntable{margin-bottom:30px;margin-top:0px;}\nTable.Gridview{border:none;white-space:nowrap; width:100%;}\n.GridviewTable{border:none;}\n.Gridview th{text-align:left;color:White;border:none;background-color:#47a3da;padding: 3px 0 3px 10px;}\n.Gridview th a:link,a:visited{color:White;text-decoration:none;}\n.Gridview td{border:none;padding: 10px 1em 10px 10px;}\n.Gridview tr{color:#666;background-color:white;text-align:left;border-bottom:1px solid #f5f5f5;}\n.Gridview .alt{background:#f8f8f8;}\n.Gridview td a:link, .GridView td a:visited {color:white;text-decoration:none;background:#666666;-webkit-border-radius:5px;-moz-border-radius:3px;-ms-border-radius:3px;-o-border-radius:3px;border-radius:3px;padding:8px 12px;}\n.Gridview a:visited {background:#666666; }\n\nTable.log{white-space:normal; word-wrap:break-word;}\n.log tr {border:none; }\n.log td {padding:2px; }\n\nTable.gv-confirm { width:600px; }\nTable.gv_members{margin-left:15px;border-left:2px solid #e8e8e8;border-bottom:2px solid #e8e8e8; margin-top:-5px; width:780px;}\nTable.gv_parts{margin-left:15px;border-left:2px solid #e8e8e8;border-bottom:2px solid #e8e8e8; margin-top:-5px; width:1000px; padding:0;}\n.gv_parts th {background-color:#eee;